import { Elysia } from "elysia";
import { makeBadge } from "badge-maker";
import type { ExtendedFormat } from "../types/extended-badge-maker";
import { ZenlessZoneZeroClient } from "../lib/enka-network";
import { HTTPError } from "ky";

const logoBase64 =
    "data:image/webp;base64,";

const zzzMakeBadge = (extendedFormat: ExtendedFormat) => {
    const { logo: _, ...format } = extendedFormat;
    return makeBadge({
        ...format,
        logoBase64: extendedFormat.logo === "zzz" ? logoBase64 : undefined,
    });
};

const zzz = new ZenlessZoneZeroClient();

const ErrorBadge: ExtendedFormat = {
    label: "error",
    message: "Bad Request",
    color: "red",
    logo: "zzz",
};

const NotFoundBadge: ExtendedFormat = {
    label: "error",
    message: "User not found",
    color: "red",
    logo: "zzz",
};

const InternalServerErrorBadge: ExtendedFormat = {
    label: "error",
    message: "Internal Server Error",
    color: "red",
    logo: "zzz",
};

export const ZzzGen = new Elysia({ prefix: "/zzz" }).get(
    "/:uid/:type",
    async (context) => {
        const {
            params: { uid, type },
            query,
        } = context;

        if (type !== "il") {
            return new Response(zzzMakeBadge(ErrorBadge), {
                headers: {
                    "Content-Type": "image/svg+xml",
                },
                status: 400,
            });
        }

        const allowedStyles = [
            "plastic",
            "flat",
            "flat-square",
            "for-the-badge",
            "social",
        ];
        if (query.style && !allowedStyles.includes(query.style)) {
            return new Response(zzzMakeBadge(ErrorBadge), {
                headers: {
                    "Content-Type": "image/svg+xml",
                },
                status: 400,
            });
        }

        // biome-ignore lint/suspicious/noExplicitAny: <explanation>
        let userData: any;
        try {
            userData = await zzz.get(uid);
        } catch (error) {
            if (error instanceof HTTPError) {
                if (error.response.status === 404) {
                    return new Response(zzzMakeBadge(NotFoundBadge), {
                        headers: {
                            "Content-Type": "image/svg+xml",
                        },
                        status: 404,
                    });
                }
            }
            return new Response(zzzMakeBadge(InternalServerErrorBadge), {
                headers: {
                    "Content-Type": "image/svg+xml",
                },
                status: 500,
            });
        }

        if (type === "il") {
            const data: number =
                userData.PlayerInfo.SocialDetail.ProfileDetail.Level;
            return new Response(
                zzzMakeBadge({
                    label: query.title || "Zenless Zone Zero",
                    message: `${query.prefix || "IL"} ${data.toString()}`,
                    color: query.colour || "blue",
                    style:
                        (query.style as
                            | "flat"
                            | "plastic"
                            | "flat-square"
                            | "for-the-badge"
                            | "social") || "flat",
                    logo: query.logo,
                }),
                {
                    headers: {
                        "Content-Type": "image/svg+xml",
                    },
                },
            );
        }
    },
);
